catalog_script_client.script:
function onLoad() {
	var firstName;
	var lastName;
	var fullName;
	//var date = now();
	firstName = g_user.firstName;
	lastName = g_user.lastName; 
	fullName = firstName + " " + lastName;

	var sMsg='<header>';
		//sMsg=sMsg+ '<h2>Testing new layout, please disregard...</h2>';
		//sMsg=sMsg+ 'Welcome to the Sensitive Information Assistant Tool ' + fullName + '!  Please read and consider the information below:<br/>';
		sMsg=sMsg+ ' <h3> Welcome to the CSA Questionnaire, ' + fullName + '! </h3>';


	spModal.open({
			//gets rid of header bar with title and the cancel x
			noDismiss: true,
			//not needed due to above line
			title: 'Important notes before starting:',
			//msg built from above code
			message: sMsg,
			//size of modal pop up, large
			size: 'lg',
			//makes sure user cannot click off of the modal to get rid of it
			backdrop: 'static',
			//makes sure user cannot click escape on keyboard to get rid of modal
			"keyboard": false,
			//adds 2 buttons on modal, one agree and other disagree
			buttons: [
				//{label:'Disagree', cancel: true}
                {label:'Acknowledge', primary: true}
			],
		}).then(function(answer){
			g_form.setDisplay('does_your_contract_have_a_requirement_to_mark_cui',true);
} , function(answer){
			//g_form.addInfoMessage("Disagree Pressed");
			//If they pressed disagree, closes tab
			g_form.addErrorMessage("You must accept the terms and conditions in order to continue");
});
}
