sys_script.script:
(function executeRule(current, previous /*null when async*/) {


		var arrayUtil = new global.ArrayUtil();
		var curAppList = current.related_business_applications;
		var prevAppList = previous.related_business_applications;

		var curAppArray = new Array();
		if(curAppList != '') curAppArray = curAppList.split(',');
		var prevAppArray = new Array();
		if(prevAppList !='') prevAppArray = prevAppList.split(',');

		var prevNotInCur = new Array();
		prevNotInCur = arrayUtil.diff(prevAppArray, curAppArray);
		var curNotInPrev = new Array();
		curNotInPrev = arrayUtil.diff(curAppArray, prevAppArray);

// 		prevNotInCur.forEach(function(busApp)
// 		{
// 			gs.addInfoMessage('Not in Current: ' + busApp);
// 		});

		var grAppsToCntr = new GlideRecord('x_631631_grc_contr_bus_app_to_contract');
		var grAppsToPrgm = new GlideRecord('x_631631_grc_contr_bus_app_to_program');
		var gaPrgInContract = new GlideAggregate('x_631631_grc_contr_contract');
		var gaAppsToPrgm = new GlideAggregate('x_631631_grc_contr_bus_app_to_program');

		curNotInPrev.forEach(function(busApp)
		{
// 			gs.addInfoMessage('Not in Prev: ' + busApp);
			grAppsToCntr.initialize();
			grAppsToCntr.setValue('business_application', busApp);
			grAppsToCntr.setValue('contract', current.sys_id);
			grAppsToCntr.insert();

			gaAppsToPrgm.addAggregate('COUNT');
			gaAppsToPrgm.addQuery('business_application', busApp);
			gaAppsToPrgm.addQuery('program', current.x_631631_sc_grc_co_grc_contract);
			gaAppsToPrgm.query();
			gaAppsToPrgm.next();
gs.addInfoMessage('gaApptToPgm - ' + current.x_631631_sc_grc_co_grc_contract + ': ' + gaAppsToPrgm.getAggregate('COUNT'))	;		
			if(gaAppsToPrgm.getAggregate('COUNT') < 1)
			{
				gaAppsToPrgm.initialize();
				grAppsToPrgm.setValue('business_application', busApp);
				grAppsToPrgm.setValue('program', current.x_631631_sc_grc_co_grc_contract);
				grAppsToPrgm.insert();
			}

		});

		prevNotInCur.forEach(function(busApp)
		{
// 			grAppsToCntr = '';

			gaPrgInContract.addQuery('related_business_applications', 'CONTAINS', busApp);
			gaPrgInContract.addQuery('x_631631_sc_grc_co_grc_contract', current.x_631631_sc_grc_co_grc_contract);
			gaPrgInContract.addAggregate('COUNT');
			gaPrgInContract.query();
			gaPrgInContract.next();
gs.addInfoMessage('Delete has next: ' + gaPrgInContract.getAggregate('COUNT'));
			if(gaPrgInContract.getAggregate('COUNT') == 1)
			{
				gaAppsToPrgm = new GlideRecord('x_631631_grc_contr_bus_app_to_program');
				grAppsToPrgm.addQuery('business_application', busApp);
				grAppsToPrgm.addQuery('program', current.x_631631_sc_grc_co_grc_contract);
				grAppsToPrgm.query();
				if(grAppsToPrgm.hasNext())
				{
					grAppsToPrgm.next();
					grAppsToPrgm.deleteRecord();				
				}
			}

			grAppsToCntr.addQuery('business_application', busApp);
			grAppsToCntr.addQuery('contract', current.sys_id);
			grAppsToCntr.query();
			if(grAppsToCntr.next())
			{
				grAppsToCntr.deleteRecord();
			}


		//Update Program M2M



});

// 		gs.addInfoMessage('C List: ' + curAppList);
// 		gs.addInfoMessage('P List: ' + prevAppList);

// 		gs.addInfoMessage('P not in C: ' + prevNotInCur.toString());
// 		gs.addInfoMessage('C not in P: ' + curNotInPrev.toString());





})(current, previous);
