sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// 2d27804e971611108c6e7b771153afe1
	//Get sys_id of assessible record related to current record
	var grAsmtType = new GlideRecord('asmt_metric_type');
	grAsmtType.addQuery('name', 'Supplier Assessment');
	grAsmtType.query();
	grAsmtType.next();
	gs.info('asmtType: ' + grAsmtType.getValue(('sys_id')));


	var grAssessable = new GlideRecord('asmt_assessable_record');
	var encodedString = 'metric_type=2d27804e971611108c6e7b771153afe1^name=REPLACE';
	encodedString = encodedString.replaceAll('REPLACE', current.supplier_name);
	gs.info('encodedString: ' + encodedString);
// 	grAssessable.addQuery('source', current.getUniqueValue());
// 	grAssessable.addQuery('metric_type', '2d27804e971611108c6e7b771153afe1');
// 	grAssessable.addQuery('name', 'Company 101');
	grAssessable.addEncodedQuery(encodedString);
	grAssessable.query();
	gs.info('type from ar: ' + grAssessable.getDisplayValue('metric_type'));
	gs.info('recourdCount: ' + grAssessable.getRowCount());

	if(grAssessable.next())
	{
		var assessor = current.poc_supplier_risk_validator;
		var assess = (new global.SNC.AssessmentCreation()).createAssessments('',current.getUniqueValue(), assessor);
		gs.info('assess: ' + assess);
	}


})(current, previous);
