sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Get to the validation record and check if this is the primary supplier

	var supplierId = current.sn_grc_profile.applies_to;
	var childAssessmentIDs = [];

	var grValidation = new GlideRecord('x_631631_grc_contr_supplier_validation');
	grValidation.addQuery('supplier',supplierId );
	grValidation.query();


	if(grValidation.next())
	{
		if(!grValidation.secondary_suppliers.nil())
		{
			var primarySupplier = grValidation.supplier;
			var supplierList = grValidation.secondary_suppliers;
			var supplierIds = supplierList.split(',');
// 			var childAssessmentIDs = [];

			//Get the scoping assessment for the primary supplier
			var grScopingAsmt = new GlideRecord('asmt_assessment_instance');
			grScopingAsmt.addQuery('sn_grc_profile.applies_to', primarySupplier);
			grScopingAsmt.addQuery('metric_type', 'b559f318974221108c6e7b771153af85');
			grScopingAsmt.query();
			while (grScopingAsmt.next())
			{
				var primaryScopingAsmtId = grScopingAsmt.sys_id;
			}

			//Iterate through all secondary suppliers 
			for(var i=0;i<supplierIds.length;i++)
			{
				// Create the entity if one does not already exist
				var entityId = new ContractSlfUtilBase().createSupplierEntity(grValidation.assigned_to, supplierIds[i]);

// 				gs.addInfoMessage('EntityID: ' + entityId);

				// Create the Scoping assessment if it doesn't already exist
				var assessmentId = new ContractSlfUtilBase().createSupplierScopingAssessment(entityId);
				childAssessmentIDs.push(assessmentId);

				gs.addInfoMessage('Assessment ID: ' + assessmentId);

			}
	gs.addInfoMessage('Copying Scoping questions');		
			// Copy responses to assessments for secondary suppliers
			var asmtCopyReturn = new ContractSlfUtilBase().copyAssessmentResponses(primaryScopingAsmtId, childAssessmentIDs, 'b559f318974221108c6e7b771153af85');
		}
	}



})(current, previous);
