sys_script_include.script:
var ContractAjaxUtilBase = Class.create();
ContractAjaxUtilBase.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	copySuppliers: function()
	{
		var source = this.getParameter('source');
		var destination = this.getParameter('destination');
		var inclApps = this.getParameter('inclApps');
		var inclSuppliers = this.getParameter('inclSuppliers');

		var grContract = new GlideRecord('x_631631_grc_contr_contract');
		var rec = grContract.get(source);
		if(rec)
		{
			var grDestContract = new GlideRecord('x_631631_grc_contr_contract');
			var destRec = grDestContract.get(destination);
			if(destRec)
			{
				if(inclSuppliers)
				{
					grDestContract.cui_suppliers = grContract.cui_suppliers;
				}			
				if(inclApps)
				{
					gs.addInfoMessage('App Copy is not yet implemented');
				}
				grDestContract.update();
				gs.addInfoMessage('Reload form to view updated app & supplier lists');
			}
		}

	},
    type: 'ContractAjaxUtilBase'
});
