sys_script_include.script:
var ContractAjaxUtilBase = Class.create();
ContractAjaxUtilBase.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	copySuppliers: function()
	{
		var source = this.getParameter('source');
		var destination = this.getParameter('destination');
		var inclApps = this.getParameter('inclApps');
		var inclSuppliers = this.getParameter('inclSuppliers');

		var grContract = new GlideRecord('x_631631_grc_contr_contract');
		var rec = grContract.get(source);
		if(rec)
		{
			var grDestContract = new GlideRecord('x_631631_grc_contr_contract');
			var destRec = grDestContract.get(destination);
			if(destRec)
			{
				if(inclSuppliers)
				{
					grDestContract.cui_suppliers = grContract.cui_suppliers;
				}			
				if(inclApps)
				{
					gs.addInfoMessage('App Copy is not yet implemented');
				}
				grDestContract.update();
				gs.addInfoMessage('Reload form to view updated app & supplier lists');
			}
		}

	},

	getInstanceName: function()
	{
		var instanceName = gs.getProperty('instance_name');

		return instanceName;
	},

	setOverride: function()
	{
		var source = this.getParameter('source');
		var overrideDate = this.getParameter('overrideDate');
		var overrideType = this.getParameter('overrideType');
		var confidenceLevel = this.getParameter('confidenceLevel');
		var currentDate = new GlideDateTime();
		var userID = gs.getUserID();

		var aryIds = source.split(',');
		var grSLF = new GlideRecord('x_631631_grc_contr_supplier');

		for(var i=0;i<aryIds.length;i++)
		{
			grSLF.get(aryIds[i]);
// 			gs.addInfoMessage('Setting values for ' + grSLF.supplier_name + ': Override Date - ' + overrideDate +'  Override Type - ' + overrideType + '  Confience Level - ' + confidenceLevel);

			grSLF.override_sprs_confidence_level = confidenceLevel;
			grSLF.override_sprs_assessment_date = overrideDate;
			if(overrideType=='compliance' || overrideType == 'both')
			{
				grSLF.enterprise_dfars_supplier_override_exists = 'true';
				grSLF.corporate_compliance_override_date = currentDate;
				grSLF.corporate_compliance_override_set_by = userID;
			}
			if(overrideType=='risk' || overrideType == 'both')
			{
				grSLF.enterprise_csc_risk_supplier_override_exists = 'true';
				grSLF.corporate_risk_override_date = currentDate;
				grSLF.corporate_risk_override_set_by = userID;
			}
			grSLF.update();
		}
	},
    type: 'ContractAjaxUtilBase'
});
