sys_script_include.script:
var ContractNofificationUtilBase = Class.create();
ContractNofificationUtilBase.prototype = {
    initialize: function() {
    },


scheduledNotification: function() {
	this._scheduledNotification();
},

_scheduledNotification: function() {
	this._sendBaselineDue();
},

_sendBaselineDue: function() {

	var baselineReminder = new GlideRecord('x_631631_grc_contr_contract');
	baselineReminder.addEncodedQuery('baseline_dateISEMPTY');
	baselineReminder.query();

	while (baselineReminder.next())
	{
		gs.eventQueue('x_631631_grc_contr.baseline_date_approac', baselineReminder.sys_id);
	}
},


    type: 'ContractNofificationUtilBase'
};
