sys_script_include.script:
var CSAUtils = Class.create();
CSAUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

getBA: function()
{
	var commonID = this.getParameter('commonID');
	var ba = 'nothing';
  var grBusApp = new GlideRecord('cmdb_ci_business_app');
   grBusApp.addQuery('x_631631_grc_contr_common_id',commonID);
   grBusApp.query();

   if(grBusApp.next())
   {
		var grCompany = new GlideRecord('core_company');
		grCompany.get(grBusApp.company);
		ba = grCompany.getDisplayValue();
   }
   else
   {
	ba = 'not found';
   }

   return ba;
} ,

    type: 'CSAUtils'
});
