sys_ui_page.client_script:
function invokePromptCallBack(type) {
	var gdw = GlideDialogWindow.get();
	if (type == 'ok')
	{
		var refValue = my_reference_element.value;
		var appValue = document.getElementById("includeApps").value;
		var supplierValue = document.getElementById("includeSuppliers").value;
		var proceed = (supplierValue == 'true' || appValue=='true') && refValue != '';

		if(proceed)
		{
			var contractAjax = new GlideAjax('ContractAjaxUtilBase');
			contractAjax.addParam('sysparm_name', 'copySuppliers');
			contractAjax.addParam('source', refValue);
			contractAjax.addParam('destination', g_form.getUniqueValue());
			contractAjax.addParam('inclApps',document.getElementById("includeApps").value);
			contractAjax.addParam('inclSuppliers',document.getElementById("includeSuppliers").value);
			contractAjax.getXMLAnswer();

			gdw.destroy();
			return false;
		}
		else
		{
			alert('Please select a source contract and at least one checkbox');
		}
	}
	else
	{
		gdw.destroy();
		return false;		
	}
}
