sys_ui_page.client_script:
function invokePromptCallBack(type) {
	var gdw = GlideDialogWindow.get();
	if (type == 'ok')
	{
		var overrideDate = document.getElementById("override_date").value;
		var selectedIds = document.getElementById("testList").value;
		var overrideType = document.getElementById("ovr_type").value;
		var confidenceLevel = document.getElementById("conf_lvl").value;
		var proceed = true;

		if(proceed)
		{
			var contractAjax = new GlideAjax('ContractAjaxUtilBase');
			contractAjax.addParam('sysparm_name', 'setOverride');
			contractAjax.addParam('source', selectedIds);
			contractAjax.addParam('overrideDate', overrideDate);
			contractAjax.addParam('overrideType', overrideType);
			contractAjax.addParam('confidenceLevel', confidenceLevel);
			contractAjax.getXMLAnswer();
// 			alert(overrideDate);
// 			alert(selectedIds);
// 			alert(g_form.getUniqueValue());
			gdw.destroy();
			return false;
		}
		else
		{
			alert('Please select a source contract and at least one checkbox');
		}
	}
	else
	{
		gdw.destroy();
		return false;		
	}
}
